mapper.update:
update employee
set password = #{password,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR},
  department_sn = #{departmentSn,jdbcType=CHAR},
  post = #{post,jdbcType=VARCHAR}
where sn = #{sn,jdbcType=CHAR}

mapper.select:
select count(1)
from employee
where sn = #{sn} and password = #{oldPassword}

select e.*, d.sn dsn, d.name dname from employee e left join department d on d.sn=e.department_sn
where e.sn is not NULL

  and e.department_sn = #{dsn}


  and e.post = #{post}
